// 处理前端发送的数据
const inputData = $input.all();
const webhookData = inputData[0].json;

// 提取消息内容
const messages = webhookData.messages || [];
const clientId = webhookData.clientId || 'default';
const meta = webhookData.meta || {};

// 获取最后一条用户消息
const lastUserMessage = messages.filter(msg => msg.role === 'user').pop();
const userInput = lastUserMessage ? lastUserMessage.content : '你好';

// 构建系统提示词
let systemPrompt = '你是一个专业的诗词鉴赏助手，擅长解释古诗词的含义、背景和艺术特色。请用简洁易懂的语言回答用户的问题。';
if (meta.system) {
  systemPrompt = meta.system;
}

// 构建完整的对话上下文
let fullContext = systemPrompt + '\n\n';
for (const msg of messages) {
  if (msg.role === 'user') {
    fullContext += '用户: ' + msg.content + '\n';
  } else if (msg.role === 'assistant') {
    fullContext += '助手: ' + msg.content + '\n';
  }
}
fullContext += '请回答用户的问题: ' + userInput;

return {
  json: {
    input: fullContext,
    clientId: clientId,
    originalMessages: messages,
    userInput: userInput,
    systemPrompt: systemPrompt,
    model: meta.model || 'deepseek-chat',
    temperature: meta.temperature || 0.7
  }
};

// 处理AI响应
const inputData = $input.all();
const aiResponse = inputData[0].json;

// 从Process Input节点获取原始数据（通过合并输入）
let originalData = {};
if (inputData.length > 1) {
  originalData = inputData[1].json;
} else {
  // 如果没有第二个输入，尝试从AI响应中提取
  originalData = {
    clientId: 'default',
    model: 'deepseek-chat'
  };
}

// 提取AI回复内容
let reply = '';
if (typeof aiResponse === 'string') {
  reply = aiResponse;
} else if (aiResponse && aiResponse.content) {
  reply = aiResponse.content;
} else if (aiResponse && aiResponse.text) {
  reply = aiResponse.text;
} else if (aiResponse && aiResponse.message) {
  reply = aiResponse.message;
} else {
  reply = '抱歉，我暂时无法回答这个问题。';
}

// 清理回复内容
reply = reply.trim();
if (reply.startsWith('助手: ')) {
  reply = reply.substring(3);
}
if (reply.startsWith('用户: ')) {
  reply = reply.substring(3);
}

return {
  json: {
    ok: true,
    reply: reply,
    clientId: originalData.clientId || 'default',
    sessionId: originalData.clientId || 'default',
    model: originalData.model || 'deepseek-chat',
    timestamp: Date.now(),
    originalResponse: aiResponse
  }
};
